import './game.scss';
import React,{useState} from 'react';
import Scores from './scores/scores';
import Canvas from './canvas/canvas';
import Chat from './chat/chat';
import Words from './words/words';
import Timer from './timer/timer';

const worddict = require('./words/words.json');

function Game({username,roomname,socket}){
   /* const [wordlist,setwordlist] = useState([]);
    function generate_random_list(){
        let temp = wordlist;
        for(let i=0;i<3;i++){
            temp.push(worddict.english[parseInt(Math.random()*worddict.english.length)]);
        }
        setwordlist(temp);
    }*/

    const [currentword,setcurrentword] = useState("");
    const [popup,setpopup] = useState(true);
    
    function generate_random_word(){
        return worddict.english[parseInt(Math.random()*worddict.english.length)];
    }

    function set_random_word(s){
        setcurrentword(s);
        setpopup(false);
    }
    var s1 = generate_random_word();
    var s2 = generate_random_word();
    var s3 = generate_random_word();
    console.log(s1,s2,s3);
    return(
       
    <div className='Game'>
       
        {popup?
        <div>
        <button onClick={()=>set_random_word(s1)}>{s1}</button>
        <button onClick={()=>set_random_word(s2)}>{s2}</button>
        <button onClick={()=>set_random_word(s3)}>{s3}</button>
        </div>
        :null}
        <div>
            <Timer
                userName={username}
                socket={socket}
                drawer={"d"}
                timelimit={20}
            />
        </div>
        <div className='left'>
            <Scores
                roomname={roomname}
                socket={socket}
            />
        </div>
        <div>
             
            <div>
                <Words
                    currentword = {currentword}
                    username = {username}
                    drawer={"d"}
                    />
            </div>
            <div>
                <Canvas
                    userName={username}
                    socket={socket}
                    drawer={"d"}
                />
            </div>
        </div>
        <div className='right'>
            <Chat
                username={username} 
                roomname={roomname} 
                socket={socket}
            /> 
        </div>
    </div>);
}

export default Game;
