import './game.scss';
import React,{useState} from 'react';
import Scores from './scores/scores';
import Canvas from './canvas/canvas';
import Chat from './chat/chat';
import Words from './words/words';

const worddict = require('./words/words.json');

function Game({username,roomname,socket}){
   /* const [wordlist,setwordlist] = useState([]);
    function generate_random_list(){
        let temp = wordlist;
        for(let i=0;i<3;i++){
            temp.push(worddict.english[parseInt(Math.random()*worddict.english.length)]);
        }
        setwordlist(temp);
    }*/
    const [currentword1,setcurrentword1] = useState("");
    const [currentword2,setcurrentword2] = useState("");
    const [currentword3,setcurrentword3] = useState("");
    
    function generaterandomword(){
        setcurrentword1(worddict.english[parseInt(Math.random()*worddict.english.length)]);
        setcurrentword2(worddict.english[parseInt(Math.random()*worddict.english.length)]);
        setcurrentword3(worddict.english[parseInt(Math.random()*worddict.english.length)]);
    }
    
    return(

    <div className='Game'>
        <div className='left'>
            <Scores
                roomname={roomname}
                socket={socket}
            />
        </div>
        <div>
            <div>
                <button onClick={generaterandomword}>Press Me</button>
            </div> 
            <div>
                <Words
                    currentword1 = {currentword1}
                    currentword2 = {currentword2}
                    currentword3 = {currentword3}
                    username = {username}
                    drawer={"d"}
                    />
            </div>
            <div>
                <Canvas
                    userName={username}
                    socket={socket}
                    drawer={"d"}
                />
            </div>
        </div>
        <div className='right'>
            <Chat
                username={username} 
                roomname={roomname} 
                socket={socket}
            /> 
        </div>
    </div>);
}

export default Game;
