  const [seconds, setSeconds] = useState(0);
//  const [isActive, setIsActive] = useState(false);

  /*function toggle() {
    setIsActive(!isActive);
  }*/

  function reset() {
    setSeconds(0);
    //setIsActive(false);
  }

  socket.on("time_change",(data)=>{
    setSeconds(data.seconds)
  });


  socket.on("Start_Timer",() => {
    reset();
   // setIsActive(true);
  })





