import './game.scss';
import React,{useState} from 'react';
import Scores from './scores/scores';
import Canvas from './canvas/canvas';
import Chat from './chat/chat';
import Words from './words/words';

const worddict = require('./words/words.json');

function Game({username,roomname,socket}){
    const [wordlist,setwordlist] = useState([]);
    function generate_random_list(){
        let temp = wordlist;
        for(let i=0;i<3;i++){
            temp.push(worddict.english[parseInt(Math.random()*worddict.english.length)]);
        }
        setwordlist([...temp]);
    }
    return(

    <div className='Game'>
        <div className='left'>
            <Scores
                roomname={roomname}
                socket={socket}
            />
        </div>
        <div>
            <div>
                <button onClick={generate_random_list}>Press Me</button>
            </div> 
            <div>
                <Words
                    currentword = {currentword}
                    username = {username}
                    drawer={"d"}
                    />
            </div>
            <div>
                <Canvas
                    userName={username}
                    socket={socket}
                    drawer={"d"}
                />
            </div>
        </div>
        <div className='right'>
            <Chat
                username={username} 
                roomname={roomname} 
                socket={socket}
            /> 
        </div>
    </div>);
}

export default Game;
