import './game.scss';
import React,{useState} from 'react';
import Scores from './scores/scores';
import Canvas from './canvas/canvas';
import Chat from './chat/chat';
import Words from './words/words';



function Game({username,roomname,socket}){
    const [currentword,setcurrentword] = useState("");
    function generaterandomword(){
        setcurrentword("rammukakka");
    }
    return(

    <div className='Game'>
        <div className='left'>
            <Scores
                roomname={roomname}
                socket={socket}
            />
        </div> 
        <div>
            <Words/>
        </div>
        <div>
            <Canvas
                userName={username}
                socket={socket}
                drawer={"d"}
            />
        </div>
        <div className='right'>
            <Chat
                username={username} 
                roomname={roomname} 
                socket={socket}
            /> 
        </div>
    </div>);
}

export default Game;
